mapper.select:
  	select 
  		u.u_id, u.username, u.password, u.user_role,
  		r.role_id, r.roleCode, r.roleName, r.type,
	    rg.u_id, rg.rg_name, rg.type
  	from user u 
  		left join t_user_role ur on u.u_id = ur.user_id
  		left join t_role r on ur.role_id = r.role_id
  		left join t_role_group rg on rg.u_id = r.rg_id
    where "1=1" 

    		and  u.u_id = #{uId,jdbcType=INTEGER}


    		and  u.username = #{username,jdbcType=VARCHAR}


    		and u.password = #{password,jdbcType=VARCHAR}


    		and u.user_role = #{userRole,jdbcType=VARCHAR}

mapper.update:
update user
set username = #{username,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  user_role = #{userRole,jdbcType=VARCHAR}
where u_id = #{uId,jdbcType=INTEGER}
